ee:message.ee:set-payload:
%dw 2.0
output application/json
---
if (payload.tools?)
    (payload update {
        case .tools -> "not used"
    }) ++ { plan: "General Knowledge" } ++ "chainsOfThoughts": vars.chainsOfThoughts
else
    payload ++ { tools: "used" } ++ vars.multiTools ++ "chainsOfThoughts": vars.chainsOfThoughts

%dw 2.0
output application/json
---
if (vars.multitools."input-required" == true)
  {
  	"id": vars.taskId,
    "contextId": vars.contextId,
    "status": {
      "state": "input-required",
      "message": {
        "role": "agent",
        "parts": [
          {
            "kind": "text",
            "text": payload.response
          }
        ],
        "messageId": vars.messageId,
        "taskId": vars.taskId,
        "contextId": vars.contextId
      },
    },
    "kind": "task"
  }
else
  {
  	"id": vars.taskId,
    "contextId": vars.contextId,
    "status": {
      "state": "completed"
    },
    "artifacts": [
      {
        "artifactId": vars.taskId,
        "name": "task",
        "parts": [
          {
            "kind": "text",
            "text": payload.response
          }
        ]
      }
    ],
    "kind": "task",
    "metadata": {}
  }
