ee:message.ee:set-payload:
%dw 2.0
output application/json
---
{
  tools: payload map (item) -> {
    "type": "function",
    function: {
      name: lower((item.payload.name) as String replace /\s+/ with ""),
      description: write({
      	"agentName": item.payload.name, 
      	"agentDescription": item.payload.description, 
      	"agentSkills": item.payload.skills
      }, "application/json"),
      parameters: {
        "type": "object",
        properties: {
        	"prompt": {
		      "type": "string"
		    },
	    },
        required: ["prompt"]
      }
    }
  }
}

set-variable.value:
#[%dw 2.0
output application/json
---
{
	tools: vars.agents.tools map ((item, index) -> 
	    if (item.function.name == vars.currAgent)
	        item ++ { agentUrl: vars.agentUrl }
	    else
	        item
	)
}]

ee:message.ee:set-payload:
%dw 2.0
output application/json
var agentforceAgent= 
    {
        url: p('agent.agentforce.url'),
        agentType: "Agentforce",
        authentication: {
            "type": "client-credentials",
            "clientId": p('agent.agentforce.clientId'),
            "clientSecret": p('agent.agentforce.clientSecret')
        }
    }
var einsteinAgent= 
    {
        url: p('agent.einstein.url'),
        agentType: "Einstein Models",
        authentication: {
            "type": "client-credentials",
            "clientId": p('agent.einstein.clientId'),
            "clientSecret": p('agent.einstein.clientSecret')
        }
    }
var crmAgent= 
    {
        url: p('agent.crm.url'),
        agentType: "MuleSoft AI Chain",
        authentication: {
            "type": "client-credentials",
            "clientId": p('agent.crm.clientId'),
            "clientSecret": p('agent.crm.clientSecret')
        }
    }
var erpAgent= 
    {
        url: p('agent.erp.url'),
        agentType: "MuleSoft AI Chain",
        authentication: {
            "type": "client-credentials",
            "clientId": p('agent.erp.clientId'),
            "clientSecret": p('agent.erp.clientSecret')
        }
    }
---
{
	agents:
		payload.agents as Array map ((item, index) -> 

    		if(item.url == p('agent.crm.url'))
    			crmAgent
     		else if(item.url == p('agent.erp.url'))
     			erpAgent
     		else if(item.url == p('agent.agentforce.url'))
     			agentforceAgent
     		else if(item.url == p('agent.einstein.url'))
     			einsteinAgent
     		else
    			item

    	)
  }

set-variable.value:
#[%dw 2.0
output application/json

var allTools = (vars.agents.tools default []) as Array
---
{
  tools: distinctBy(
    allTools filter (item) -> not (vars.agentsToDelete contains item.function.name),
    (item, index) -> item.function.name
  )
}]
